import React, { useState } from 'react';

const App = () => {
  const [symbol, setSymbol] = useState('');
  const [date, setDate] = useState('');
  const [tradeData, setTradeData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:5000/api/fetchStockData', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ symbol, date }),
      });

      if (!response.ok) {
        const errorResponse = await response.json();
        setError(errorResponse.error || 'Something went wrong.');
        setTradeData(null);
      } else {
        const data = await response.json();
        setTradeData(data);
        setError(null);
      }
    } catch (error) {
      setError('Something went wrong.');
      setTradeData(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Trade Statistics</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Stock Symbol:
          <input type="text" value={symbol} onChange={(e) => setSymbol(e.target.value)} required />
        </label>
        <br />
        <label>
          Date:
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </label>
        <br />
        <button type="submit">Fetch Data</button>
      </form>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {tradeData && (
        <div>
          <h2>Trade Data for {symbol} on {date}</h2>
          <p>Open: {tradeData.open}</p>
          <p>High: {tradeData.high}</p>
          <p>Low: {tradeData.low}</p>
          <p>Close: {tradeData.close}</p>
          <p>Volume: {tradeData.volume}</p>
        </div>
      )}
    </div>
  );
};

export default App;
