import React, { useState } from 'react';

const App = () => {
    const [symbol, setSymbol] = useState('');
    const [date, setDate] = useState('');
    const [tradeData, setTradeData] = useState(null);

    const handleSubmit = async(e) => {
        e.preventDefault();
        try {
            const response = await fetch('/api/fetchStockData', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ symbol, date }),
            });

            const data = await response.json();
            setTradeData(data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    return ( < div >
        <h1 > Trade Statistics </ h1>
		 <form onSubmit = { handleSubmit } >
			<label >Stock Symbol:
				<input type = "text"value = { symbol }onChange = {    (e) => setSymbol(e.target.value)}required/>
			</label> <br/><label >Date:<input type = "date"value = { date }onChange = {    (e) => setDate(e.target.value)}required/></label> <br/>
				<button type = "submit" > Fetch Data </ button> </ form > {    tradeData && ( <        div >        <        h2 > Trade Datafor { symbol }        on { date } </h2> <        p > Open: { tradeData.open } </p > < p > High: { tradeData.high } </p > < p > Low: { tradeData.low } </p > < p > Close: { tradeData.close } </p > < p > Volume: { tradeData.volume } </p > </div >    )} </div>
    );
};

export default App;
