import React, { useState, useRef } from 'react';
import './App.css'; // Import the custom CSS file for styling

const App = () => {
  const [symbol, setSymbol] = useState('');
  const [date, setDate] = useState('');
  const [tradeData, setTradeData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [prevSymbol, setPrevSymbol] = useState(''); // Store the previous stock symbol
  const [prevDate, setPrevDate] = useState(''); // Store the previous date
  const [dataFetched, setDataFetched] = useState(false); // Track whether data has been fetched

  // Get tomorrow's date in YYYY-MM-DD format
  const tomorrow = new Date();
  tomorrow.setDate(tomorrow.getDate() + 1);
  const tomorrowDate = tomorrow.toISOString().split('T')[0];

  // Use useRef to store the timeouts
  const symbolChangeTimeoutRef = useRef(null);
  const dateChangeTimeoutRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:5000/api/fetchStockData', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ symbol, date }),
      });

      if (!response.ok) {
        const errorResponse = await response.json();
        setError(errorResponse.error || 'Something went wrong.');
        setTradeData(null);
      } else {
        const data = await response.json();
        setTradeData(data);
        setError(null);
        setPrevSymbol(symbol); // Update the previous symbol and date
        setPrevDate(date);
      }
    } catch (error) {
      setError('Something went wrong.');
      setTradeData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleSymbolChange = (e) => {
    setSymbol(e.target.value);
    setDataFetched(false); // Reset dataFetched state when the symbol changes

    // Clear any previous timeout and set a new one
    clearTimeout(symbolChangeTimeoutRef.current);
    symbolChangeTimeoutRef.current = setTimeout(() => {
      setTradeData(null); // Clear previous output after a brief delay
      setError(null); // Clear any errors after a brief delay
    }, 1000);
  };

  const handleDateChange = (e) => {
    setDate(e.target.value);
    setDataFetched(false); // Reset dataFetched state when the date changes

    // Clear any previous timeout and set a new one
    clearTimeout(dateChangeTimeoutRef.current);
    dateChangeTimeoutRef.current = setTimeout(() => {
      setTradeData(null); // Clear previous output after a brief delay
      setError(null); // Clear any errors after a brief delay
    }, 1000);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (symbol !== prevSymbol || date !== prevDate) {
      // Only update trade data if either the stock symbol or date has changed
      setDataFetched(false); // Reset dataFetched state on form submit
      handleSubmit(e);
    }
    else{
      setDataFetched(false); // Reset dataFetched state on form submit

    }
  };

  return (
    <div className="container">
      <h1 className="title">Trade Statistics</h1>
      {dataFetched && !loading && (
        <div className="message">
          <p>Data is already fetched below</p>
        </div>
      )}
      <form className="form" onSubmit={handleFormSubmit}>
        <div className="input-group">
          <label className="label">Stock Symbol:</label>
          <input
            className="input"
            type="text"
            value={symbol}
            onChange={handleSymbolChange}
            required
          />
        </div>
        <div className="input-group">
          <label className="label">Date:</label>
          <input
            className="input"
            type="date"
            value={date}
            onChange={handleDateChange}
            max={tomorrowDate} // Set the maximum date to tomorrow
            required
          />
        </div>
        <button className="submit-button" type="submit" disabled={loading}>
          {loading ? 'Fetching...' : 'Fetch Data'}
        </button>
      </form>
      {error && <p className="error">{error}</p>}
      {tradeData && (
        <div className="trade-data">
          <h2>Trade Data for {prevSymbol} on {date}</h2>
          <p>Open: {tradeData.open}</p>
          <p>High: {tradeData.high}</p>
          <p>Low: {tradeData.low}</p>
          <p>Close: {tradeData.close}</p>
          <p>Volume: {tradeData.volume}</p>
        </div>
      )}
    </div>
  );
};

export default App;
